import styled from 'styled-components/native';

export const BackButton = styled.TouchableOpacity`
    padding: 10px;
    background-color: #191a30;
    margin-top: 60px;
    flex-direction: row;
    align-items: center;
`;

export const Name = styled.Text`
    margin-left: 8px;
    color: #FFF;
    font-size: 18px;
    font-weight: bold;
`;
